mapper.update:
UPDATE MEMBER
SET
	M_PASSWORD = #{password}
WHERE
	M_ID = #{id}

UPDATE MEMBER
SET
	M_NAME = #{name}
WHERE
	M_ID = #{id}

UPDATE MEMBER
SET
	M_PHONE = #{phone}
WHERE
	M_ID = #{id}

UPDATE MEMBER
SET
	M_EMAIL = #{email}
WHERE
	M_ID = #{id}

UPDATE MEMBER
SET
	M_GENDER = #{gender}
WHERE
	M_ID = #{id}

UPDATE MEMBER
SET
	M_HOST_REQUEST = #{host}
WHERE
	M_ID = #{id}

mapper.select:
SELECT M_EMAIL 
FROM MEMBER 
WHERE M_ID = #{id}
